[Code: typescript]
// Admin / User
interface Admin {
  id: string;
  username: string;
  email: string;
  fullName: string;
  role: 'SUPER_ADMIN' | 'ADMIN' | 'VIEWER';
  status: 'ACTIVE' | 'INACTIVE' | 'LOCKED';
  lastLoginAt?: Date;
  createdAt: Date;
  updatedAt: Date;
}

// Shop Owner
interface ShopOwner {
  id: string;
  email: string;
  fullName: string;
  phone?: string;
  shopName: string;
  shopAddress?: string;
  avatarUrl?: string;
  status: 'ACTIVE' | 'INACTIVE' | 'PENDING';
  emailVerified: boolean;
  createdAt: Date;
  updatedAt: Date;
}

// POI
interface POI {
  id: string;
  nameVi: string;
  nameEn?: string;
  descriptionVi: string;
  descriptionEn?: string;
  latitude: number;
  longitude: number;
  triggerRadius: number;
  category: 'MAIN' | 'SUB';
  status: 'DRAFT' | 'ACTIVE' | 'INACTIVE';
  media: POIMedia[];
  createdBy?: string;
  ownerId?: string;  // Shop Owner FK
  createdAt: Date;
  updatedAt: Date;
}

// Media
interface POIMedia {
  id: string;
  poiId: string;
  type: 'IMAGE' | 'AUDIO';
  language: 'VI' | 'EN' | 'ALL';
  url: string;
  filename: string;
  sizeBytes: number;
  durationSeconds?: number;
  mimeType: string;
  orderIndex: number;
}

// Tour
interface Tour {
  id: string;
  nameVi: string;
  nameEn?: string;
  descriptionVi: string;
  descriptionEn?: string;
  thumbnailUrl?: string;
  estimatedDuration?: number;
  status: 'DRAFT' | 'ACTIVE' | 'INACTIVE';
  pois: TourPOI[];
  createdBy: string;
  createdAt: Date;
  updatedAt: Date;
}

interface TourPOI {
  id: string;
  tourId: string;
  poiId: string;
  poi?: POI;
  orderIndex: number;
}

// ============================================
// TOURIST USER DOMAIN (Optional Login)
// ============================================

interface TouristUser {
  id: string;
  deviceId: string;
  email?: string;
  displayName?: string;
  authProvider?: 'EMAIL' | 'GOOGLE' | 'FACEBOOK' | 'APPLE';
  languagePref: 'VI' | 'EN';
  autoPlayAudio: boolean;
  pushToken?: string;
  pushEnabled: boolean;
  createdAt: Date;
  lastActiveAt: Date;
}

interface ViewHistory {
  id: string;
  userId: string;
  poiId: string;
  poi?: POI;
  viewedAt: Date;
  viewDurationSec?: number;
  audioPlayed: boolean;
  audioCompleted: boolean;
  triggerType: 'GPS' | 'QR' | 'MANUAL';
}

interface UserFavorite {
  id: string;
  userId: string;
  poiId: string;
  poi?: POI;
  createdAt: Date;
}

// ============================================
// SYSTEM SUPPORT
// ============================================

interface PasswordResetToken {
  id: string;
  adminId: string;
  token: string;
  expiresAt: Date;
  usedAt?: Date;
  createdAt: Date;
}

interface QRCode {
  id: string;
  poiId: string;
  codeData: string;
  format: 'URL' | 'POI_ID';
  isActive: boolean;
  scanCount: number;
  generatedAt: Date;
  lastScannedAt?: Date;
}

interface TriggerLog {
  id: string;
  userId?: string;
  poiId: string;
  triggerType: 'GPS' | 'QR' | 'MANUAL';
  userAction: 'ACCEPTED' | 'SKIPPED' | 'IGNORED' | 'AUTO_DISMISSED';
  userLat?: number;
  userLng?: number;
  distanceMeters?: number;
  triggeredAt: Date;
}

// ============================================
// SYSTEM SUPPORT
// ============================================
